result:
query($id: ID!, $page:Int) {
  author(id: $id) {
    name
    path
    bio
    image(width:150, height:150)
    facebook
    twitter
    linkedin
    belongsTo(perPage: 5, page: $page) @paginate {
      totalCount
      pageInfo {
        totalPages
        currentPage
      }
      edges {
        node {
          ... on Blog {
            title
            excerpt
            image(width:800)
            path
            timeToRead
            humanTime : created(format:"DD MMM YYYY")
            datetime : created
            category {
              id
              title
            }
            author {
              id
              name
              image(width:64, height:64, fit:inside)
              path
            }
          }
        }
      }
    }
  }  
}
